// BackgroundImage.js
import './styles.css';
import { TodoCounter } from './TodoCounter';
import { TodoSearch } from './TodoSearch';  
import { TodoList } from './TodoList';
import { TodoItem } from './TodoItem';
import { CreateTodoButton } from './CreateTodoButton';
import React, { useState } from "react"; //Los estados deben estar en el componente papa y ser pasados al componente hijo


const array = [{tarea:'Estudiar 30 minutos al dia', estado:'Pendiente'},
              {tarea:'Dormir 8 horas al dia', estado:'Pendiente'},
              {tarea:'Tomar mate una vez por dia', estado:'Completado'},
              {tarea:'Ir al Sauna', estado:'Pendiente'},
              {tarea:'Pestañear', estado:'Completado'},
              {tarea:'Terminar el Curso de React', estado:'Pendiente'},
              {tarea:'Comer la comida', estado:'Completado'},
];

//Vamos a usar localstorage para tener persistencia de datos, sin usar base de datos relacional ni no relacionales
let stringifiedArray = localStorage.setItem('Version_1', JSON.stringify(array)); //-> para inicializar la version_1 del Localstorage
//tambien vamos a crear un customhook para manejar todo lo relacionado a localstorage:
function useLocalStorage(initialValue){
  let localstoragedArray = localStorage.getItem('Version_1');
  let parsedArray;

  if (!localstoragedArray) {
    localStorage.setItem('Version_1',JSON.stringify(initialValue));  //si Version_1 (primera vez que se abre la app) = Vacio -> array vacio, pq si no hay que mostrar, la app crashea!
    parsedArray = [];
  } else {
    parsedArray = JSON.parse(localstoragedArray);
  }
  const [item, setItem] = useState(parsedArray); //asignamos el valor vacio o inicial al estado del customhook
  //necesitamos la funcion que actualice el estado de los elementos y a la vez el localstorage tal que exista persistencia de datos
   //al refrescar la pagina:
  const saveStateTodos = (newTodos) => {
    localStorage.setItem('Version_1', JSON.stringify(newTodos));
    setItem(newTodos);
  }
  return [item, saveStateTodos];  //saveStateTodos es el array persistente de todos, item es la lista de todos parseada
}

//Componente React
function BackgroundImage () {
  let [search, setSearch] = useState("");
  let [todos, saveStateTodos]= useLocalStorage([]);
  console.log(saveStateTodos);
  //Funcion para obtener la estadistica de cantidad de completados y totales
  const reducedResult = todos.reduce((accumulator, currentTask) => {
  accumulator.totalTasks++;
  if (currentTask.estado === 'Completado') {
    accumulator.completedTasks++;
  }
  return accumulator;
  }, { totalTasks: 0, completedTasks: 0 });

  // Filtra el array 'todos' basado en el valor de 'search'
  const filteredTodos = todos.filter(todo => {
    // Verifica si la tarea incluye la palabra introducida en 'search'
    return todo.tarea.toLowerCase().includes(search.toLowerCase());
  });

  //Creamos la funcion para actualizar el estado del todo a completado para pasarselo al todoItem tal que al presionar el
  //icono de tickeado aparezca completado
  const CompletarTodo = (tarea) => {
    const newTodos = [...todos];  //creamos un nuevo array con la lista de todos tal que podamos actualizarla
    const todoIndex = newTodos.findIndex(
      (todo) => todo.tarea === tarea);
    newTodos[todoIndex].estado = "Completado";  //cambiamos el estado del todo apretado
    //setTodos(newTodos); -> antes de usar localstorage
    saveStateTodos(newTodos);
  }
  //Creamos la funcion para actualizar el estado del vector todos a eliminar el todo al presionar el icono cancel en el todoItem
  const eliminarTodo =(tarea) => {
    let newTodos = [...todos];  //creamos un nuevo array con la lista de todos tal que podamos actualizarla
    const todoIndex = newTodos.findIndex(
      (todo) => todo.tarea === tarea);
    if (todoIndex !== -1) {
      newTodos.splice(todoIndex, 1); // Eliminamos el elemento en el índice todoIndex
      //setTodos(newTodos); // Actualizamos el estado con el nuevo array sin el elemento; -> antes de usar localstorage
      saveStateTodos(newTodos);
    }
  }

  return (
    <div className="background-image">
      <div className="todo-list-container"> 
        <TodoCounter tasks={reducedResult.totalTasks} completed={reducedResult.completedTasks}/>
        <TodoSearch 
          search={search} setSearch={setSearch}
        />
        <TodoList />
          {filteredTodos.map(todo => (    //usamos el estado inicial de los todos para renderizarlos
            <TodoItem key={todo.tarea} texto={todo.tarea} state={todo.estado} 
              onComplete={()=> CompletarTodo(todo.tarea)}
              onDelete={()=> eliminarTodo(todo.tarea)}
            />
          ))}
        <TodoList />
        <CreateTodoButton />
      </div>
    </div>
  );
};

export default BackgroundImage;
