import checkIcon from './icons8-done.svg';
import cancelIcon from './icons8-cancel.svg';
import React, { useState } from "react"; //Los estados deben estar en el componente papa y ser pasados al componente hijo


function TodoItem( { texto, state ,setTodos, todos}){
  const handleComplete = () => {
    // Aquí actualizamos el estado del elemento actual a "Completado"
    const updatedTodos = todos.map(todo => {
      if (todo.texto === texto) {
        return { ...todo, state: "Completado" };
      }
      return todo;
    });
    setTodos(updatedTodos);
  };

  const handleCancel = () => {
    // Aquí actualizamos el estado del elemento actual a "Cancelado"
    const updatedTodos = todos.map(todo => {
      if (todo.texto === texto) {
        return { ...todo, state: "Cancelado" };
      }
      return todo;
    });
    setTodos(updatedTodos);
  };
  return (
    <li className={`todo-item ${state === 'Pendiente' ? 'tarea-pendiente' : 'tarea-completada'}`}>
      <img className="checked-icon" src={checkIcon} alt="Check Icon" 
        onClick={handleComplete}
      />
      <p>{ texto }</p>
      <p>{ state }</p>
      <img className="cancel-icon" src={cancelIcon} alt="Cancel Icon" onClick={handleCancel}/>
    </li>
  );
}
export { TodoItem }
