// BackgroundImage.js
import React from 'react';
import './styles.css';
import { TodoCounter } from './TodoCounter';
import { TodoSearch } from './TodoSearch';  
import { TodoList } from './TodoList';
import { TodoItem } from './TodoItem';
import { CreateTodoButton } from './CreateTodoButton';

const array = [{tarea:'Estudiar 30 minutos al dia', estado:'Pendiente'},
              {tarea:'Dormir 8 horas al dia', estado:'Pendiente'},
              {tarea:'Tomar mate una vez por dia', estado:'Completado'},
              {tarea:'Ir al Sauna', estado:'Pendiente'}
];

const BackgroundImage = () => {
  return (
    <div className="background-image">
      <div className="todo-list-container"> 
        <TodoCounter tasks='4' completed={25}/>
        <TodoSearch />
        <TodoList />
          {array.map(todo => (
            <TodoItem key={todo.tarea} texto={todo.tarea} state={todo.estado}/>
          ))}
        <TodoList />
        <CreateTodoButton />
      </div>
    </div>
  );
};

export default BackgroundImage;
