import checkIcon from './icons8-done.svg';
import cancelIcon from './icons8-cancel.svg';

function TodoItem( { texto, state }){
  return (
    <li className={`todo-item ${state === 'Pendiente' ? 'tarea-pendiente' : 'tarea-completada'}`}>
      <img OnClick={()=> console.log('Apretaste el icono!')} className="checked-icon" src={checkIcon} alt="Check Icon" />
      <p>{ texto }</p>
      <p>{ state }</p>
      <img className="cancel-icon" src={cancelIcon} alt="Cancel Icon" />
    </li>
  );
}
export { TodoItem }
